import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useAuth } from '../store/auth';
import axios from 'axios';
import { toast } from 'react-hot-toast';

export default function Profile() {
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const { register, handleSubmit, setValue, formState: { errors } } = useForm({
    defaultValues: {
      name: user?.name || '',
      email: user?.email || '',
      skills: user?.skills?.join(', ') || '',
      seniority: user?.seniority || 'junior',
      department: user?.department || ''
    }
  });

  const onSubmit = async (data) => {
    try {
      // Convert skills string to array
      const formattedData = {
        ...data,
        skills: data.skills.split(',').map(skill => skill.trim()).filter(Boolean)
      };

      await axios.put(`http://localhost:5000/api/engineers/${user._id}`, formattedData);
      toast.success('Profile updated successfully');
      setIsEditing(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to update profile');
    }
  };

  const toggleEdit = () => {
    if (isEditing) {
      // Reset form to current user data
      setValue('name', user.name);
      setValue('email', user.email);
      setValue('skills', user.skills?.join(', '));
      setValue('seniority', user.seniority);
      setValue('department', user.department);
    }
    setIsEditing(!isEditing);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-2xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">My Profile</h1>
          <button
            onClick={toggleEdit}
            className="px-4 py-2 text-sm font-medium rounded-md border"
            type="button"
          >
            {isEditing ? 'Cancel' : 'Edit Profile'}
          </button>
        </div>

        <div className="bg-white rounded-lg shadow-sm p-6">
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            {/* Name */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Name
              </label>
              {isEditing ? (
                <>
                  <input
                    type="text"
                    {...register('name', { required: 'Name is required' })}
                    className="w-full p-2 border rounded-md"
                  />
                  {errors.name && (
                    <p className="text-red-500 text-sm mt-1">{errors.name.message}</p>
                  )}
                </>
              ) : (
                <p className="text-gray-900">{user.name}</p>
              )}
            </div>

            {/* Email */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Email
              </label>
              {isEditing ? (
                <>
                  <input
                    type="email"
                    {...register('email', { 
                      required: 'Email is required',
                      pattern: {
                        value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                        message: 'Invalid email address'
                      }
                    })}
                    className="w-full p-2 border rounded-md"
                  />
                  {errors.email && (
                    <p className="text-red-500 text-sm mt-1">{errors.email.message}</p>
                  )}
                </>
              ) : (
                <p className="text-gray-900">{user.email}</p>
              )}
            </div>

            {/* Skills */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Skills (comma-separated)
              </label>
              {isEditing ? (
                <>
                  <input
                    type="text"
                    {...register('skills', { required: 'At least one skill is required' })}
                    className="w-full p-2 border rounded-md"
                    placeholder="React, JavaScript, Node.js"
                  />
                  {errors.skills && (
                    <p className="text-red-500 text-sm mt-1">{errors.skills.message}</p>
                  )}
                </>
              ) : (
                <div className="flex flex-wrap gap-2">
                  {user.skills?.map((skill, index) => (
                    <span
                      key={index}
                      className="px-2 py-1 bg-gray-100 text-gray-700 rounded text-sm"
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              )}
            </div>

            {/* Seniority */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Seniority Level
              </label>
              {isEditing ? (
                <>
                  <select
                    {...register('seniority', { required: 'Seniority level is required' })}
                    className="w-full p-2 border rounded-md"
                  >
                    <option value="junior">Junior</option>
                    <option value="mid">Mid-Level</option>
                    <option value="senior">Senior</option>
                  </select>
                  {errors.seniority && (
                    <p className="text-red-500 text-sm mt-1">{errors.seniority.message}</p>
                  )}
                </>
              ) : (
                <p className="text-gray-900 capitalize">{user.seniority}</p>
              )}
            </div>

            {/* Department */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Department
              </label>
              {isEditing ? (
                <>
                  <input
                    type="text"
                    {...register('department', { required: 'Department is required' })}
                    className="w-full p-2 border rounded-md"
                    placeholder="Engineering"
                  />
                  {errors.department && (
                    <p className="text-red-500 text-sm mt-1">{errors.department.message}</p>
                  )}
                </>
              ) : (
                <p className="text-gray-900">{user.department}</p>
              )}
            </div>

            {/* Save Button */}
            {isEditing && (
              <button
                type="submit"
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors"
              >
                Save Changes
              </button>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}
