import { create } from 'zustand';
import axios from 'axios';

export const useAuth = create((set) => ({
  user: null,
  isAuthenticated: false,
  loading: false,
  error: null,

  login: async (email, password) => {
    try {
      set({ loading: true, error: null });
      console.log('Attempting login with:', { email }); // Debug log
      
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password
      });
      
      console.log('Login response:', response.data); // Debug log
      
      const { user, token } = response.data;
      // Ensure we have a valid user object with _id
      if (!user || !user._id) {
        console.error('Invalid user data received:', response.data); // Debug log
        throw new Error('Invalid user data received');
      }
      
      localStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      set({ user, isAuthenticated: true, loading: false });
      return true;
    } catch (error) {
      console.error('Login error:', error.response || error); // Debug log
      set({ 
        error: error.response?.data?.message || 'Login failed', 
        loading: false 
      });
      return false;
    }
  },

  logout: () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    set({ user: null, isAuthenticated: false });
  },

  checkAuth: async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        set({ isAuthenticated: false });
        return;
      }

      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      const response = await axios.get('http://localhost:5000/api/auth/me');
      set({ user: response.data, isAuthenticated: true });
    } catch (error) {
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
      set({ user: null, isAuthenticated: false });
    }
  },

  isManager: () => {
    const state = useAuth.getState();
    return state.user?.role === 'manager';
  },

  isEngineer: () => {
    const state = useAuth.getState();
    return state.user?.role === 'engineer';
  }
}));
