import React from 'react';
import { useStore } from '../store';
import { useAuth } from '../store/auth';
import { AssignmentTimeline } from '../components/ui/assignment-timeline';
import { CapacityBar } from '../components/ui/capacity-bar';

export default function MyAssignments() {
  const { assignments, loading, fetchAssignments } = useStore();
  const { user } = useAuth();

  React.useEffect(() => {
    fetchAssignments();
  }, [fetchAssignments]);

  if (loading) {
    return <div className="container mx-auto px-4 py-8">Loading...</div>;
  }

  // Filter assignments for the current user and sort them by date
  const myAssignments = assignments.filter(a => a.engineerId._id === user._id);
  const currentDate = new Date();
  
  const currentAssignments = myAssignments.filter(a => 
    new Date(a.startDate) <= currentDate && new Date(a.endDate) >= currentDate
  );

  const upcomingAssignments = myAssignments.filter(a => 
    new Date(a.startDate) > currentDate
  );

  // Calculate total capacity used
  const totalCapacityUsed = currentAssignments.reduce(
    (sum, assignment) => sum + assignment.allocationPercentage,
    0
  );

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">My Assignments</h1>
      
      {/* Total Capacity Bar */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
        <h2 className="text-lg font-semibold mb-4">Current Capacity</h2>
        <CapacityBar 
          capacity={totalCapacityUsed}
          maxCapacity={100}
        />
        <p className="text-sm text-gray-600 mt-2">
          {totalCapacityUsed}% of capacity utilized
        </p>
      </div>

      {/* Current Assignments */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
        <h2 className="text-lg font-semibold mb-4">Current Projects</h2>
        {currentAssignments.length > 0 ? (
          <AssignmentTimeline assignments={currentAssignments} />
        ) : (
          <p className="text-gray-500">No current assignments</p>
        )}
      </div>

      {/* Upcoming Assignments */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h2 className="text-lg font-semibold mb-4">Upcoming Projects</h2>
        {upcomingAssignments.length > 0 ? (
          <AssignmentTimeline assignments={upcomingAssignments} />
        ) : (
          <p className="text-gray-500">No upcoming assignments</p>
        )}
      </div>
    </div>
  );
}
