import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../store/auth';
import { useState } from 'react';
import axios from 'axios';

function Login() {
  const { register, handleSubmit, setValue, formState: { errors } } = useForm();
  const navigate = useNavigate();
  const { login } = useAuth();
  const [role, setRole] = useState('engineer');

  const onSubmit = async (data) => {
    const success = await login(data.email, data.password);
    if (success) {
      // Get the user's role from the auth store
      const { user } = useAuth.getState();
      if (user.role === 'manager') {
        navigate('/projects'); // Managers see the projects page first
      } else {
        navigate('/assignments'); // Engineers see their assignments first
      }
    }
  };

  const toggleRole = (newRole) => {
    setRole(newRole);
    setValue('email', newRole === 'manager' ? 'manager@gmail.com' : 'engineer@gmail.com');
    setValue('password', '12345'); // Default password for demo
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8 p-8 bg-white rounded-lg shadow">
        <h2 className="text-3xl font-bold text-center">Log In</h2>
        
        <div className="flex justify-center space-x-4 mb-6">
          <button
            type="button"
            onClick={() => toggleRole('engineer')}
            className={`px-4 py-2 rounded-md ${
              role === 'engineer'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Engineer
          </button>
          <button
            type="button"
            onClick={() => toggleRole('manager')}
            className={`px-4 py-2 rounded-md ${
              role === 'manager'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Manager
          </button>
        </div>
        
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              {...register("email", { required: true })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
            {errors.email && <span className="text-red-500 text-sm">Email is required</span>}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Password</label>
            <input
              type="password"
              {...register("password", { required: true })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
            {errors.password && <span className="text-red-500 text-sm">Password is required</span>}
          </div>

          <button
            type="submit"
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Sign In as {role === 'manager' ? 'Manager' : 'Engineer'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
