import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useStore } from '../store';
import { useAuth } from '../store/auth';
import { SkillTag } from '../components/ui/skill-tag';
import { CapacityBar } from '../components/ui/capacity-bar';
import { SeniorityBadge } from '../components/ui/seniority-badge';
import axios from 'axios';

function Assignments() {
  const navigate = useNavigate();
  const { assignments, projects, loading, fetchAssignments, fetchProjects, createAssignment } = useStore();
  const { user, isManager } = useAuth();
  const [activeTab, setActiveTab] = useState('projects'); // 'projects', 'assignments', 'capacity'
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const [engineers, setEngineers] = useState([]);
  const [selectedEngineerId, setSelectedEngineerId] = useState('');
  const [selectedEngineerCapacity, setSelectedEngineerCapacity] = useState(null);
  const [allocationPercentage, setAllocationPercentage] = useState(50);
  const [assignmentDates, setAssignmentDates] = useState({
    startDate: '',
    endDate: ''
  });
  const [role, setRole] = useState('developer');
  const [error, setError] = useState('');
  const [loadingEngineers, setLoadingEngineers] = useState(false);

  useEffect(() => {
    fetchAssignments();
    fetchProjects();
  }, [fetchAssignments, fetchProjects]);

  // Fetch engineers when modal is opened
  useEffect(() => {
    const fetchEngineers = async () => {
      if (isModalOpen && currentProject) {
        setLoadingEngineers(true);
        try {
          const response = await axios.get('http://localhost:5000/api/engineers');
          setEngineers(response.data);
        } catch (err) {
          setError('Failed to load engineers');
        }
        setLoadingEngineers(false);
      }
    };
    fetchEngineers();
  }, [isModalOpen, currentProject]);

  // Fetch engineer capacity when an engineer is selected
  useEffect(() => {
    const fetchEngineerCapacity = async () => {
      if (selectedEngineerId) {
        try {
          const response = await axios.get(`http://localhost:5000/api/engineers/${selectedEngineerId}/capacity`);
          setSelectedEngineerCapacity(response.data);
          // Adjust allocation percentage based on available capacity
          if (response.data.available < allocationPercentage) {
            setAllocationPercentage(Math.floor(response.data.available / 25) * 25);
          }
        } catch (err) {
          setError('Failed to load engineer capacity');
        }
      }
    };
    fetchEngineerCapacity();
  }, [selectedEngineerId]);

  // Calculate engineer workloads
  const engineerWorkloads = React.useMemo(() => {
    return engineers.map(engineer => {
      const engineerAssignments = assignments.filter(a => a.engineerId._id === engineer._id);
      const totalAllocation = engineerAssignments.reduce((sum, a) => sum + a.allocationPercentage, 0);
      return {
        ...engineer,
        currentWorkload: totalAllocation,
        assignments: engineerAssignments
      };
    });
  }, [engineers, assignments]);

  const handleEditAssignment = async (assignment) => {
    setCurrentProject(assignment.projectId);
    setSelectedEngineerId(assignment.engineerId._id);
    setAllocationPercentage(assignment.allocationPercentage);
    setAssignmentDates({
      startDate: new Date(assignment.startDate).toISOString().split('T')[0],
      endDate: new Date(assignment.endDate).toISOString().split('T')[0]
    });
    setRole(assignment.role);
    setIsModalOpen(true);
  };

  const handleDeleteAssignment = async (assignmentId) => {
    if (window.confirm('Are you sure you want to delete this assignment?')) {
      try {
        await axios.delete(`http://localhost:5000/api/assignments/${assignmentId}`);
        await fetchAssignments(); // Refresh the assignments list
      } catch (err) {
        setError('Failed to delete assignment');
      }
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col mb-6">
        <h1 className="text-3xl font-bold mb-6">Resource Management</h1>
        
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('projects')}
              className={`${
                activeTab === 'projects'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap pb-4 px-1 border-b-2 font-medium text-sm`}
            >
              Project Assignments
            </button>
            <button
              onClick={() => setActiveTab('assignments')}
              className={`${
                activeTab === 'assignments'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap pb-4 px-1 border-b-2 font-medium text-sm`}
            >
              Current Assignments
            </button>
            <button
              onClick={() => setActiveTab('capacity')}
              className={`${
                activeTab === 'capacity'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap pb-4 px-1 border-b-2 font-medium text-sm`}
            >
              Capacity Overview
            </button>
          </nav>
        </div>
      </div>

      {/* Assignment Modal */}
      {isModalOpen && currentProject && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg w-full max-w-md">
            <h2 className="text-2xl font-bold mb-4">Assign Engineer to {currentProject.name}</h2>
            
            {error && <div className="mb-4 text-red-500">{error}</div>}

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Select Engineer
                </label>
                {loadingEngineers ? (
                  <div className="text-center py-2">Loading engineers...</div>
                ) : (
                  <select
                    value={selectedEngineerId}
                    onChange={(e) => {
                      setSelectedEngineerId(e.target.value);
                      setAllocationPercentage(50); // Reset allocation when engineer changes
                    }}
                    className="w-full p-2 border rounded-md"
                  >
                    <option value="">Choose an engineer</option>
                    {engineers
                      .filter(engineer => {
                        // Filter engineers based on required skills
                        return currentProject.requiredSkills.some(skill => 
                          engineer.skills.includes(skill)
                        );
                      })
                      .map(engineer => (
                        <option key={engineer._id} value={engineer._id}>
                          {engineer.name}
                        </option>
                      ))}
                  </select>
                )}
              </div>

              {selectedEngineerCapacity && (
                <div className="mb-4">
                  <CapacityBar 
                    allocation={selectedEngineerCapacity.allocated}
                    maxCapacity={100}
                  />
                  <p className="text-sm text-gray-600 mt-1">
                    Available Capacity: {selectedEngineerCapacity.available}%
                  </p>
                </div>
              )}

              {selectedEngineerId && selectedEngineerCapacity && (
                <>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Allocation Percentage
                    </label>
                    <input
                      type="number"
                      min="25"
                      max="100"
                      step="25"
                      value={allocationPercentage}
                      onChange={(e) => setAllocationPercentage(Number(e.target.value))}
                      className="w-full p-2 border rounded-md"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Start Date
                    </label>
                    <input
                      type="date"
                      value={assignmentDates.startDate}
                      onChange={(e) => setAssignmentDates(prev => ({
                        ...prev,
                        startDate: e.target.value
                      }))}
                      min={new Date().toISOString().split('T')[0]}
                      className="w-full p-2 border rounded-md"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      End Date
                    </label>
                    <input
                      type="date"
                      value={assignmentDates.endDate}
                      onChange={(e) => setAssignmentDates(prev => ({
                        ...prev,
                        endDate: e.target.value
                      }))}
                      min={assignmentDates.startDate || new Date().toISOString().split('T')[0]}
                      className="w-full p-2 border rounded-md"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Role
                    </label>
                    <select
                      value={role}
                      onChange={(e) => setRole(e.target.value)}
                      className="w-full p-2 border rounded-md"
                    >
                      <option value="developer">Developer</option>
                      <option value="tech-lead">Tech Lead</option>
                      <option value="architect">Architect</option>
                    </select>
                  </div>
                </>
              )}

              <div className="flex justify-end space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => {
                    setIsModalOpen(false);
                    setCurrentProject(null);
                    setSelectedEngineerId('');
                    setRole('developer');
                    setError('');
                  }}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={async () => {
                    try {
                      // Validate capacity
                      if (selectedEngineerCapacity.available < allocationPercentage) {
                        setError(`Engineer only has ${selectedEngineerCapacity.available}% capacity available`);
                        return;
                      }

                      // Create the assignment through the API
                      await axios.post('http://localhost:5000/api/assignments', {
                        projectId: currentProject._id,
                        engineerId: selectedEngineerId,
                        allocationPercentage,
                        startDate: new Date(assignmentDates.startDate),
                        endDate: new Date(assignmentDates.endDate),
                        role: role
                      });

                      setIsModalOpen(false);
                      setCurrentProject(null);
                      setSelectedEngineerId('');
                      setSelectedEngineerCapacity(null);
                      fetchAssignments(); // Refresh the assignments list
                    } catch (err) {
                      setError(err.response?.data?.message || 'Failed to create assignment');
                    }
                  }}
                  disabled={!selectedEngineerId || !assignmentDates.startDate || !assignmentDates.endDate}
                  className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md disabled:opacity-50"
                >
                  Assign Engineer
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {/* Projects Tab */}
          {activeTab === 'projects' && (
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <table className="min-w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Project Name
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Team (Assigned/Total)
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Required Skills
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Duration
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {projects.map((project) => (
                    <tr key={project._id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">
                          {project.name}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {assignments.filter(a => a.projectId._id === project._id).length} / {project.teamSize}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex gap-2">
                          {project.requiredSkills.map((skill, index) => (
                            <span
                              key={index}
                              className="bg-gray-100 text-gray-700 px-2 py-1 rounded text-sm"
                            >
                              {skill}
                            </span>
                          ))}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {new Date(project.startDate).toLocaleDateString()} -
                          {new Date(project.endDate).toLocaleDateString()}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {project.status}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        {isManager() && project.teamSize > assignments.filter(a => a.projectId._id === project._id).length && (
                          <button 
                            onClick={() => {
                              setCurrentProject(project);
                              setIsModalOpen(true);
                            }}
                            className="text-blue-600 hover:text-blue-900"
                          >
                            Assign Engineer
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Current Assignments Tab */}
          {activeTab === 'assignments' && (
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-4">Current Assignments</h2>
                <div className="space-y-4">
                  {assignments.map((assignment) => (
                    <div key={assignment._id} className="border rounded-lg p-4">
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="font-medium">{assignment.projectId.name}</h3>
                          <p className="text-sm text-gray-500">
                            {assignment.engineerId.name} - {assignment.allocationPercentage}% allocation
                          </p>
                          <p className="text-sm text-gray-500">
                            {new Date(assignment.startDate).toLocaleDateString()} - {new Date(assignment.endDate).toLocaleDateString()}
                          </p>
                        </div>
                        <div className="flex space-x-2">
                          <button
                            onClick={() => handleEditAssignment(assignment)}
                            className="px-3 py-1 text-sm bg-gray-100 text-gray-700 rounded hover:bg-gray-200"
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleDeleteAssignment(assignment._id)}
                            className="px-3 py-1 text-sm bg-red-100 text-red-700 rounded hover:bg-red-200"
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Capacity Overview Tab */}
          {activeTab === 'capacity' && (
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-4">Team Capacity Overview</h2>
                <div className="space-y-6">
                  {engineerWorkloads.map((engineer) => (
                    <div key={engineer._id} className="border rounded-lg p-4">
                      <div className="flex justify-between items-center mb-2">
                        <div>
                          <h3 className="font-medium">{engineer.name}</h3>
                          <div className="flex items-center space-x-2">
                            <SeniorityBadge seniority={engineer.seniority} />
                            {engineer.skills.map((skill) => (
                              <SkillTag key={skill} skill={skill} />
                            ))}
                          </div>
                        </div>
                        <p className="text-sm font-medium">
                          Total Allocation: {engineer.currentWorkload}%
                        </p>
                      </div>
                      <CapacityBar allocation={engineer.currentWorkload} />
                      {engineer.assignments.length > 0 && (
                        <div className="mt-4">
                          <h4 className="text-sm font-medium mb-2">Current Projects:</h4>
                          <div className="space-y-2">
                            {engineer.assignments.map((assignment) => (
                              <div key={assignment._id} className="text-sm text-gray-600">
                                {assignment.projectId.name} ({assignment.allocationPercentage}%)
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Assignments;
