import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useStore } from '../store';
import { useAuth } from '../store/auth';
import { SkillTagList } from '../components/ui/skill-tag';
import { CapacityBar } from '../components/ui/capacity-bar';
import { SeniorityBadge } from '../components/ui/seniority-badge';
import axios from 'axios';

function Engineers() {
  const navigate = useNavigate();
  const { engineers, loading, fetchEngineers } = useStore();
  const { isManager } = useAuth();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingEngineer, setEditingEngineer] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    seniority: 'junior',
    skills: [],
    employmentType: 'full-time'
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  React.useEffect(() => {
    fetchEngineers();
  }, [fetchEngineers]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSkillsChange = (e) => {
    const value = e.target.value.trim();
    if (value && !formData.skills.includes(value)) {
      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, value]
      }));
    }
  };

  const removeSkill = (skillToRemove) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter(skill => skill !== skillToRemove)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsSubmitting(true);

    try {
      if (editingEngineer) {
        await axios.put(`http://localhost:5000/api/engineers/${editingEngineer._id}`, formData);
      } else {
        await axios.post('http://localhost:5000/api/engineers', formData);
      }
      
      fetchEngineers();
      setIsModalOpen(false);
      resetForm();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save engineer');
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      seniority: 'junior',
      skills: [],
      employmentType: 'full-time'
    });
    setEditingEngineer(null);
    setError('');
  };

  const handleEdit = (engineer) => {
    setEditingEngineer(engineer);
    setFormData({
      name: engineer.name,
      email: engineer.email,
      seniority: engineer.seniority,
      skills: engineer.skills,
      employmentType: engineer.employmentType
    });
    setIsModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-8">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-blue-800">
              Engineering Team
            </h1>
            <p className="text-gray-600 mt-2">Manage and view team member profiles</p>
          </div>
          <button 
            onClick={() => {
              resetForm();
              setIsModalOpen(true);
            }}
            className="bg-gradient-to-r from-blue-600 to-blue-700 text-white px-6 py-3 rounded-lg 
                     hover:from-blue-700 hover:to-blue-800 transform hover:scale-105 transition-all duration-200
                     shadow-md hover:shadow-xl flex items-center space-x-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
            </svg>
            <span>Add New Engineer</span>
          </button>
        </div>

        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent"></div>
          </div>
        ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {engineers.map((engineer) => (
            <div key={engineer._id} 
                 className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 overflow-hidden group">
              <div className="p-6">
                <div className="flex items-center space-x-4 mb-4">
                  <div className="bg-gradient-to-br from-blue-100 to-blue-50 p-3 rounded-lg">
                    <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                        d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                    </svg>
                  </div>
                  <div>
                    <h2 className="text-xl font-semibold text-gray-800 group-hover:text-blue-600 transition-colors duration-300">
                      {engineer.name}
                    </h2>
                    <p className="text-sm text-gray-500">{engineer.email}</p>
                  </div>
                  <div className="ml-auto">
                    <SeniorityBadge level={engineer.seniority} />
                  </div>
                </div>

                <div className="mb-6">
                  <div className="text-sm font-medium text-gray-700 mb-2">Skills & Expertise</div>
                  <SkillTagList skills={engineer.skills} />
                </div>

                <div className="space-y-4">
                  <div>
                    <div className="flex items-center justify-between text-sm font-medium text-gray-700 mb-2">
                      <span>Employment Type</span>
                      <span className="px-3 py-1 rounded-full text-xs font-medium bg-blue-50 text-blue-700">
                        {engineer.employmentType === 'full-time' ? 'Full-time (100%)' : 'Part-time (50%)'}
                      </span>
                    </div>

                    <div className="mt-4">
                      <div className="text-sm font-medium text-gray-700 mb-2">Current Capacity</div>
                      <CapacityBar 
                        capacity={100 - engineer.availableCapacity} 
                        maxCapacity={engineer.employmentType === 'full-time' ? 100 : 50}
                      />
                      <p className="text-sm text-gray-500 mt-2 flex items-center">
                        <svg className="w-4 h-4 mr-1 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                            d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        <span>{engineer.availableCapacity}% Available</span>
                      </p>
                    </div>
                  </div>

                  {isManager() && (
                    <div className="mt-6 pt-4 border-t border-gray-100">
                      <button
                        onClick={() => navigate(`/engineers/${engineer._id}/edit`)}
                        className="w-full bg-gray-50 hover:bg-blue-50 text-gray-700 hover:text-blue-600 
                                 py-2 px-4 rounded-lg transition-colors duration-200 flex items-center justify-center space-x-2"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                            d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                        </svg>
                        <span>Edit Profile</span>
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
        )}
      </div>
    </div>
  );
}

export default Engineers;
