import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useAuth } from '../store/auth';
import axios from 'axios';
import { toast } from 'react-hot-toast';

export default function Profile() {
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const { register, handleSubmit, setValue, formState: { errors } } = useForm({
    defaultValues: {
      name: user?.name || '',
      email: user?.email || '',
      skills: user?.skills?.join(', ') || '',
      seniority: user?.seniority || 'junior',
      department: user?.department || ''
    }
  });

  const onSubmit = async (data) => {
    try {
      // Convert skills string to array
      const formattedData = {
        ...data,
        skills: data.skills.split(',').map(skill => skill.trim()).filter(Boolean)
      };

      await axios.put(`http://localhost:5000/api/engineers/${user._id}`, formattedData);
      toast.success('Profile updated successfully');
      setIsEditing(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to update profile');
    }
  };

  const toggleEdit = () => {
    if (isEditing) {
      // Reset form to current user data
      setValue('name', user.name);
      setValue('email', user.email);
      setValue('skills', user.skills?.join(', '));
      setValue('seniority', user.seniority);
      setValue('department', user.department);
    }
    setIsEditing(!isEditing);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-white to-gray-50">
      <div className="container mx-auto px-4 py-12">
        <div className="max-w-4xl mx-auto space-y-8">
          {/* Header Section */}
          <div className="text-center max-w-2xl mx-auto">
            <h1 className="text-4xl font-bold text-gray-900 tracking-tight">My Profile</h1>
            <p className="mt-3 text-lg text-gray-500">
              View and manage your personal information and preferences
            </p>
          </div>

          {/* Edit Button - Floating */}
          <div className="fixed bottom-8 right-8 z-10">
            <button
              onClick={toggleEdit}
              className={`group flex items-center px-6 py-3 rounded-full shadow-lg text-sm font-medium transition-all duration-200 transform hover:scale-105
                ${isEditing 
                  ? 'bg-white text-red-600 border border-red-200 hover:bg-red-50' 
                  : 'bg-blue-600 text-white hover:bg-blue-700'}`}
              type="button"
            >
              {isEditing ? (
                <>
                  <svg className="w-5 h-5 mr-2 animate-pulse" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                  Cancel Editing
                </>
              ) : (
                <>
                  <svg className="w-5 h-5 mr-2 transition-transform group-hover:rotate-12" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                  </svg>
                  Edit Profile
                </>
              )}
            </button>
          </div>

          {/* Profile Form */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
            {/* Profile Header */}
            <div className="px-8 py-6 bg-gradient-to-r from-blue-50 to-indigo-50 border-b border-gray-100">
              <div className="flex items-center space-x-5">
                <div className="h-16 w-16 rounded-full bg-gradient-to-br from-blue-500 to-indigo-600 flex items-center justify-center text-white text-2xl font-bold shadow-inner">
                  {user.name?.charAt(0).toUpperCase()}
                </div>
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">{user.name}</h2>
                  <p className="text-sm text-gray-600 mt-1 flex items-center">
                    <svg className="w-4 h-4 mr-2 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                    {user.email}
                  </p>
                </div>
              </div>
            </div>

            {/* Profile Form */}
            <form onSubmit={handleSubmit(onSubmit)} className="p-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Name Field */}
                <div className="col-span-2 md:col-span-1">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Full Name
                  </label>
                  {isEditing ? (
                    <div className="relative">
                      <input
                        type="text"
                        {...register('name', { required: 'Name is required' })}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                        placeholder="Enter your full name"
                      />
                      {errors.name && (
                        <p className="mt-1 text-sm text-red-600">{errors.name.message}</p>
                      )}
                    </div>
                  ) : (
                    <p className="text-gray-900 py-2">{user.name}</p>
                  )}
                </div>

                {/* Email Field */}
                <div className="col-span-2 md:col-span-1">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email Address
                  </label>
                  {isEditing ? (
                    <div className="relative">
                      <input
                        type="email"
                        {...register('email', { 
                          required: 'Email is required',
                          pattern: {
                            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                            message: 'Invalid email address'
                          }
                        })}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                        placeholder="your.email@example.com"
                      />
                      {errors.email && (
                        <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
                      )}
                    </div>
                  ) : (
                    <p className="text-gray-900 py-2">{user.email}</p>
                  )}
                </div>

                {/* Seniority Level */}
                <div className="col-span-2 md:col-span-1">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Seniority Level
                  </label>
                  {isEditing ? (
                    <div className="relative">
                      <select
                        {...register('seniority', { required: 'Seniority level is required' })}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors appearance-none"
                      >
                        <option value="junior">Junior Engineer</option>
                        <option value="mid">Mid-Level Engineer</option>
                        <option value="senior">Senior Engineer</option>
                      </select>
                      {errors.seniority && (
                        <p className="mt-1 text-sm text-red-600">{errors.seniority.message}</p>
                      )}
                    </div>
                  ) : (
                    <p className="text-gray-900 py-2 capitalize">{user.seniority}</p>
                  )}
                </div>

                {/* Department Field */}
                <div className="col-span-2 md:col-span-1">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Department
                  </label>
                  {isEditing ? (
                    <div className="relative">
                      <input
                        type="text"
                        {...register('department', { required: 'Department is required' })}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                        placeholder="e.g., Engineering"
                      />
                      {errors.department && (
                        <p className="mt-1 text-sm text-red-600">{errors.department.message}</p>
                      )}
                    </div>
                  ) : (
                    <p className="text-gray-900 py-2">{user.department}</p>
                  )}
                </div>

                {/* Skills Field */}
                <div className="col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Technical Skills
                  </label>
                  {isEditing ? (
                    <div className="relative">
                      <input
                        type="text"
                        {...register('skills', { required: 'At least one skill is required' })}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                        placeholder="e.g., React, JavaScript, Node.js (comma-separated)"
                      />
                      <p className="mt-1 text-xs text-gray-500">
                        Enter your skills separated by commas
                      </p>
                      {errors.skills && (
                        <p className="mt-1 text-sm text-red-600">{errors.skills.message}</p>
                      )}
                    </div>
                  ) : (
                    <div className="flex flex-wrap gap-2 py-2">
                      {user.skills?.map((skill, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-50 text-blue-700"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              {/* Action Buttons */}
              {isEditing && (
                <div className="mt-6 pt-6 border-t border-gray-100">
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={toggleEdit}
                      className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors"
                    >
                      Save Changes
                    </button>
                  </div>
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
